mapper.select:
SELECT to_char(o2.ORDDT , 'hh24') time, sum(d.TOTPRICE) as sum FROM DETAIL d, ORD o2 WHERE d.ORDNO IN ( SELECT o.ORDNO FROM ORD o WHERE
to_char(o.ORDDT , 'YYYY-MM-DD') >= #{start} AND to_char(o.ORDDT , 'YYYY-MM-DD') <= #{end}
to_char(o.ORDDT , 'YYYY-MM') >= #{start} AND to_char(o.ORDDT , 'YYYY-MM') <= #{end}
to_char(o.ORDDT , 'YYYY') >= #{start} AND to_char(o.ORDDT, 'YYYY') <= #{end}
AND o.STNO IN #{stNo}) AND D.ORDNO = O2.ORDNO GROUP BY to_char(o2.ORDDT, 'hh24') ORDER BY to_char(o2.ORDDT,'hh24')

SELECT stNm1,sum1,stNm2,sum2 FROM ( SELECT sum(d.TOTPRICE) sum1, s.stnm stNm1 FROM DETAIL d, ORD o2, STORE s WHERE d.ORDNO IN ( SELECT o.ORDNO FROM ORD o WHERE
to_char(ORDDT , 'YYYY-MM-DD') >= #{start1} AND to_char(ORDDT , 'YYYY-MM-DD') <= #{end1}
to_char(ORDDT , 'YYYY-MM') >= #{start1} AND to_char(ORDDT , 'YYYY-MM') <= #{end1}
to_char(ORDDT , 'YYYY') >= #{start1} AND to_char(ORDDT , 'YYYY') <= #{end1}
AND STNO IN
#{stNo}
) AND D.ORDNO = O2.ORDNO AND o2.STNO = s.STNO GROUP BY s.stnm), ( SELECT sum(d.TOTPRICE) sum2, s.stnm stNm2 FROM DETAIL d, ORD o2, STORE s WHERE d.ORDNO IN ( SELECT o.ORDNO FROM ORD o WHERE
to_char(ORDDT , 'YYYY-MM-DD') >= #{start2} AND to_char(ORDDT , 'YYYY-MM-DD') <= #{end2}
to_char(ORDDT , 'YYYY-MM') >= #{start2} AND to_char(ORDDT , 'YYYY-MM') <= #{end2}
to_char(ORDDT , 'YYYY') >= #{start2} AND to_char(ORDDT , 'YYYY') <= #{end2}
AND STNO IN
#{stNo}
) AND D.ORDNO = O2.ORDNO AND o2.STNO = s.STNO GROUP BY s.stnm) WHERE stnm1 = stnm2
